export const getProcessorObjects = async (): Promise<ProcessorObjectMap> => {
  const processedData: Array<ProcessorObject> = camelcaseKeys(data, { deep: true });

  return {
    [ProcessorObjectType.House]: processedData
      .filter(({ typeId }) => {
        return typeId === ProcessorObjectType.House;
      })
      .reduce((prev, currentValue) => {
        prev[currentValue.internalId] = currentValue;

        return prev;
      }, {} as any),
    [ProcessorObjectType.Nakshatra]: processedData
      .filter(({ typeId }) => {
        return typeId === ProcessorObjectType.Nakshatra;
      })
      .reduce((prev, currentValue) => {
        prev[currentValue.internalId] = currentValue;
        
        return prev;
      }, {} as any),
    [ProcessorObjectType.Sign]: processedData
      .filter(({ typeId }) => {
        return typeId === ProcessorObjectType.Sign;
      })
      .reduce((prev, currentValue) => {
        prev[currentValue.internalId] = currentValue;

        return prev;
      }, {} as any),
    [ProcessorObjectType.Planet]: processedData
      .filter(({ typeId }) => {
        return typeId === ProcessorObjectType.Planet;
      })
      .reduce((prev, currentValue) => {
        prev[currentValue.internalId] = currentValue;

        return prev;
      }, {} as any),
    [ProcessorObjectType.Karaka]: processedData
      .filter(({ typeId }) => {
        return typeId === ProcessorObjectType.Karaka;
      })
      .reduce((prev, currentValue) => {
        prev[currentValue.internalId] = currentValue;

        return prev;
      }, {} as any),
    [ProcessorObjectType.Arudhs]: processedData
      .filter(({ typeId }) => {
        return typeId === ProcessorObjectType.Arudhs;
      })
      .reduce((prev, currentValue) => {
        prev[currentValue.internalId] = currentValue;

        return prev;
      }, {} as any)
  };
};
